    private void LoadNews(NewsAdapter adapter){

        Util.getInstance().getRetrofitInstance().getNewPostCall("Bearer "+PrivateParam.getUSER().getToken()).enqueue(new Callback<NewPost>() {
            @Override
            public void onResponse(Call<NewPost> call, Response<NewPost> response) {
                Log.e(TAG, "onResponse: "+response.code() );
                if(response.body()!=null){
                    newsItems.removeAll(newsItems);
                    NewPost post = response.body();
                    for (Post post1 : post.getData()) {
                        Log.e(TAG, "onResponse: Postid: "+post1.getPostsId() );
                        newsItems.add(new News_items(post1.getPostsId(),post1.getPostsUserList()[0].getName(),
                                post1.getDateCreate(),
                                post1.getPostsUserList()[0].getImage(),
                                post1.getPostsImageList()[0].getImage(),
                                post1.getCaption(),
                                post1.isFeel(),
                                post1.getTotalFeel(),
                                post1.getTotalComment()));

                    }
                    adapter.notifyDataSetChanged();
                    swipeRefreshLayout.setRefreshing(false);
                }
            }

            @Override
            public void onFailure(Call<NewPost> call, Throwable t) {
                Log.e(TAG, "onFailure: "+t.getMessage() );
                swipeRefreshLayout.setRefreshing(false);
            }
        });
    }


